  SELECT id as teacher_uuid FROM teachers WHERE email = 'daniel.tan@school.edu.sg' LIMIT 1
),
class_info AS (
  SELECT id as class_uuid FROM classes WHERE name = '5A' AND type = 'form' LIMIT 1
)

